export default function pushInfoToDB(t){let e=localStorage.getItem("token");return fetch("https://ajax.test-danit.com/api/cards",{method:"POST",body:JSON.stringify(t),headers:{Authorization:"Bearer "+e,"Content-Type":"application/json;charset=utf-8"}}).then(t=>t.text()).catch(t=>{console.log("message of Post request: ",t.message)})}